import React, { useState, useEffect } from "react";
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBContainer,
  MDBRow,
} from "mdb-react-ui-kit";
import SpaceMission from "./graphql";

function App() {
  const [data, setData] = useState([]);

  const loadSpaceMission = async () => {
    const spaceMission = await SpaceMission.getSpaceMission(10);
    setData(spaceMission);
  };

  useEffect(() => {
    loadSpaceMission();
  }, []);

  console.log("data", data);
  return;
  <MDBContainer
    style={{
      margin: "auto",
      padding: "15px",
      maxWidth: "720px",
      alignContent: "center",
    }}
  >
    <MDBRow>
      {data.map((item, index) => (
        <>
          <MDBCard
            key={index}
            style={{ maxWidth: "22rem", maxHeight: "24rem" }}
          >
            <MDBCardImage
              src={
                item && item.ships[0] && item.ships[0].image
                  ? item.ships[0].image
                  : "https://cdnuploads.aa.com.tr/uploads/Contents/2020/05/30/thumbs_b_c_a4a6996640e91d4ff86a71f5d9d9f84b.jpg?v=225920"
              }
            ></MDBCardImage>
          </MDBCard>
        </>
      ))}
    </MDBRow>
  </MDBContainer>;
}

export default App;
